package com.naijamojiapp.app.activityimport android.os.Bundleimport android.view.Viewimport android.view.Windowimport android.view.WindowManagerimport android.widget.*import androidx.appcompat.app.AppCompatActivityimport com.naijamojiapp.Rimport com.naijamojiapp.app.utils.Utilityclass ChangeAvatarStyleActivity : AppCompatActivity(), View.OnClickListener {    var rlBack: RelativeLayout? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        Utility.instance!!.setTopBar(this,window)        setContentView(R.layout.activity_change_avtar_style)        init()    }    private fun init() {        rlBack = findViewById(R.id.rl_back)        rlBack!!.setOnClickListener(this)    }    override fun onClick(view: View?) {        when (view!!.id) {            R.id.rl_back -> {                finish()            }        }    }}