package com.naijamojiapp.app.activityimport android.content.Intentimport android.os.Bundleimport android.provider.Settingsimport android.view.Viewimport android.view.Windowimport android.view.WindowManagerimport android.widget.RelativeLayoutimport android.widget.TextViewimport androidx.appcompat.app.AppCompatActivityimport com.naijamojiapp.Rimport com.naijamojiapp.ime.latin.setup.SetupActivityclass InstuctionActivity : AppCompatActivity(), View.OnClickListener {    var rlBack: RelativeLayout? = null    var tvOpenKeyboard : TextView? = null    override fun onCreate(savedInstanceState: Bundle?) {        super.onCreate(savedInstanceState)        requestWindowFeature(Window.FEATURE_NO_TITLE)        window.setFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN, WindowManager.LayoutParams.FLAG_FULLSCREEN)        setContentView(R.layout.activity_instruction)        init()    }    private fun init() {        rlBack = findViewById(R.id.rl_back)        rlBack!!.setOnClickListener(this)        tvOpenKeyboard = findViewById(R.id.tvOpenKeyboard)        tvOpenKeyboard!!.setOnClickListener(this)    }    override fun onClick(view: View?) {        when (view!!.id) {            R.id.rl_back -> {                finish()            }            R.id.tvOpenKeyboard -> {                val intent = Intent()                intent.action = Settings.ACTION_INPUT_METHOD_SETTINGS                intent.addCategory(Intent.CATEGORY_DEFAULT)                startActivity(intent)                //startActivity(Intent(this, SetupActivity::class.java))            }        }    }}